        <script>
            var $loading = $("#loading").hide();
            //Attach the event handler to any element
            $(document)
                .ajaxStart(function () {
                    //ajax request went so show the loading image
                    $loading.show();
                })
                .ajaxStop(function () {
                    //got response so hide the loading image
                    $loading.hide();
                });
            var timeLeft = 30;
            var elem = document.getElementById("some_div");
            var timerId;
            function validate(obj) {
                var flag = true;
                var formData = $("#login_form").find(":input").not(":input[type=button]");
                $(formData).each(function () {
                    var element = $(this);
                    var val = element.val();
                    var name = element.attr("name");
                    if (val == "" || val == "0") {
                        $("#" + name).html("* required field");
                        flag = false;
                    } else {
                        $("#" + name).html("");
                    }
                });

                if (flag) {
                    $.ajax({
                        url: "<?= base_url() ?>vendor/Ajax/resendOtp",
                        type: "POST",
                        data: $("#login_form").serialize(),
                        success: function (data) {
                            var dt = $.trim(data);
                            var jsonData = $.parseJSON(dt);
                            console.dir(jsonData);
                            if (jsonData["error_code"] == 100) {
                                $("#exampleModal").modal();
                                // $("#set_otp").val(jsonData["data"]["otp"]);
                                $("#success").html(jsonData["message"]);
                                $("#error").html("");
                                startTime();
                            } else {
                                $("#error").html(jsonData["message"]);
                                $("#success").html("");
                            }
                        },
                    });
                } else {
                    return false;
                }
            }

            function verifyOtp(obj) {
                var flag = true;
                var formData = $("#otp_form").find(":input").not(":input[type=button]");
                $(formData).each(function () {
                    var element = $(this);
                    var val = element.val();
                    var name = element.attr("name");
                    if (val == "" || val == "0") {
                        $("#" + name).html("* required field");
                        flag = false;
                    } else {
                        $("#" + name).html("");
                    }
                });
                if (flag) {
                    $.ajax({
                        url: "<?= base_url() ?>vendor/Ajax/verify_otp",
                        type: "POST",
                        data: $("#otp_form").serialize() + "&email=" + $("input[name=email]").val() + "&type=3",
                        success: function (data) {
                            var dt = $.trim(data);
                            var jsonData = $.parseJSON(dt);
                            if (jsonData["error_code"] == 100) {
                                $("#errorOtp").html("");
                                $("#successOtp").html(jsonData["message"]);
                                window.location.href = "<?= base_url() ?>vendor/reset-password";
                            } else {
                                $("#errorOtp").html(jsonData["message"]);
                                $("#successOtp").html("");
                            }
                        },
                    });
                } else {
                    return false;
                }
            }

            function resend() {
                var msg = "";
                var user = $("input[name=email]").val();
                if (user) {
                    $.ajax({
                        url: "<?= base_url(); ?>vendor/Ajax/resendOtp",
                        type: "post",
                        data: "email=" + user + "&type=1",
                        success: function (data) {
                            var dt = $.trim(data);
                            var jsonData = $.parseJSON(dt);
                            msg = jsonData["message"];
                            if (jsonData["error_code"] == 100) {
                                $("#successOtp").html(msg);
                                // $("#set_otp").val(jsonData["data"]["otp"]);
                                $("#errorOtp").html("");
                                startTime();
                            } else {
                                $("#errorOtp").html(msg);
                                // $("#set_otp").val("");
                                $("#successOtp").html("");
                            }
                        },
                    });
                } else {
                    //                                        alert('Session Expired. Try logging in.');
                    return false;
                }
            }

            function startTime() {
                timeLeft = 30;
                $("#resendBtn").removeAttr("onclick");
                $("#resendBtn").css("cursor", "not-allowed");
                timerId = setInterval(countdown, 1000);
            }

            function countdown() {
                if (timeLeft == 0) {
                    elem.innerHTML = "";
                    window.clearInterval(timerId);
                    $("#resendBtn").css("cursor", "pointer");
                    $("#resendBtn").attr("onclick", "resend();");
                    //                                        break;
                } else {
                    elem.innerHTML = "00:" + timeLeft;
                    timeLeft--;
                }
            }
        </script>
