        <script>
                                function validate() {
                                    var flag = true;
                                    var c_password = '';
                                    var password = '';
                                    var formData = $("#reset_password_form").find(':input').not(':input[type=button]');
                                    $(formData).each(function () {
                                        var element = $(this);
                                        var val = element.val();
                                        var name = element.attr('name');
                                        if (val == '' || val == '0') {
                                            $('#' + name).html('* required field');
                                            flag = false;
                                        } else {
                                            if (name == 'password') {
                                                if (val.length < 8) {
                                                    flag = false;
                                                    $('#' + name).html('minimum length 8');
                                                } else {
                                                    password = val;
                                                }
                                            } else if (name == 'c_password') {
                                                c_password = val;
                                            } else {
                                                $('#' + name).html('');
                                            }
                                        }
                                    });

                                    if (flag) {
                                        if (((c_password.length > 0) && (password.length > 0))) {
                                            if (c_password != password) {
                                                flag = false;
                                                $('#password').html('Password do not match');
                                                return false;
                                            } else {
                                                $('#reset_password_form').submit();
                                            }
                                        } else {
                                            return false;
                                        }
                                    } else {
                                        return false;
                                    }
                                }

                                function showHide(obj) {
                                    var input = $('input[name=c_password]');
//                                   console.log(input);
                                    var type = $(input).attr('type');
                                    if (type == 'password') {
                                        $(input).attr('type', 'text');
                                    } else {
                                        $(input).attr('type', 'password');
                                    }
                                }
        </script>
